package br.eti.fmotta.lbm.lotteryservice.infrastructure.config.databaseimport org.jetbrains.exposed.spring.autoconfigure.ExposedAutoConfigurationimport org.jetbrains.exposed.sql.DatabaseConfigimport org.postgresql.util.PGobjectimport org.springframework.boot.autoconfigure.ImportAutoConfigurationimport org.springframework.boot.autoconfigure.jdbc.DataSourceTransactionManagerAutoConfigurationimport org.springframework.context.annotation.Beanimport org.springframework.context.annotation.Configuration@Configuration@ImportAutoConfiguration(    value = [ExposedAutoConfiguration::class],    exclude = [DataSourceTransactionManagerAutoConfiguration::class],)class ExposedConfig {    @Bean    fun databaseConfig() =        DatabaseConfig {            useNestedTransactions = true        }}class PGEnum<T : Enum<T>>(enumTypeName: String, enumValue: T?) : PGobject() {    init {        value = enumValue?.name        type = enumTypeName    }}